<template>  <!--查询-->  <el-dialog    title="单据审批"    :modal-append-to-body="false"    :append-to-body="false"    :visible.sync="param.visible"  >    <el-form label-position="left" class="demo-table-expand">      <template v-for="(item,i) in param.list">        <template v-if="item.type === 'radio'">          <el-form-item :label="item.name">            <el-radio-group v-model="queryData[item.key]">              <el-radio label="Y">通过</el-radio>              <el-radio label="N">不通过</el-radio>              <el-radio label="R">驳回</el-radio>            </el-radio-group>          </el-form-item>        </template>        <template v-else-if="item.type === 'textarea'">          <el-form-item :label="item.name">            <el-input              type="textarea"              :rows="2"              placeholder="请输入审核批语"              v-model="queryData[item.key]">            </el-input>          </el-form-item>        </template>        <template v-else>          <el-form-item :label="item.name">            <el-input size="small" v-model="queryData[item.key]"></el-input>          </el-form-item>        </template>      </template>    </el-form>    <span slot="footer" class="dialog-footer">        <el-button size="small" @click="param.visible = false">取 消</el-button>        <el-button type="primary" size="small" @click="confirmSelect">确 定</el-button>      </span>  </el-dialog></template><script>  export default {    name: "approver",    data() {      return {        param: {          visible: false,          list: [            // {"key":"name","operation":"=","value":"张三"},            { name: '审核结果', "key":"result", type: 'radio'},            { name: '审核批语', "key":"vnote", type: 'textarea'},          ]        },        dataSource: {          resultList: [            // {  }          ]        },        queryData: {          "cbillid":"",          "result":"",          "vnote":""        }      }    },    methods: {      open(row) {        if (!row.cbillid) {          return        }        this.queryData = {          cbillid: row.cbillid        }        this.param.visible = true      },      close() {        this.param.visible = false      },      confirmSelect() {        if (!this.queryData.result) {          return this.$message({ message: '请选择审核结果', duration: 1000 })        }        if (!this.queryData.vnote) {          return this.$message({ message: '请填写审核批语', duration: 1000 })        }        const data = {          action: 'approve',          data: this.queryData        }        this.$emit('confirm', data)      }    }  }</script><style scoped></style>