<template>  <el-dialog          :title="title"          :modal-append-to-body="false"          :append-to-body="false"          :visible.sync="param.visible"  >    <div class=" c_hidden" style="height: 300px;">      <el-table        class="c_h"        size="small"        height="250"        :highlight-current-row="true"        :data="param.detailData"        @row-click="projectTableClick"      >        <template v-for="(item, i) in param.detailList">          <el-table-column :key="i" :label="item.name" :prop="item.key"></el-table-column>        </template>      </el-table>    </div>    <span slot="footer" class="dialog-footer">        <el-button size="small" @click="close">取 消</el-button>        <el-button type="primary" size="small" @click="confirm">确 定</el-button>      </span>  </el-dialog></template><script>  import api from '../api'  export default {    name: "ksyhSelect",    props: {      title: {        type: String,        default: '客商银行账号'      },    },    data() {      return {        dataSource: {          resultList: [            // {  }          ]        },        api: api,        param: {          visible: false,          // -- 详情表格参数          detailList: [            { name: '银行账号', key: 'accountcode', isUse: true },            { name: '账户名称', key: 'accountname', isUse: true },          ],          // -- 详情表格数据          detailData: [],          // -- 选中tree 下部门数据          selectTableData: {},          // 搜索框          searchValue: ''        },        queryData: {          "cbillid":"",          "result":"",          "vnote":""        }      }    },    methods: {      open(ks) {        this.pkCust = ks        this.param.selectTableData = {}        this.getDataList()        this.param.visible = true      },      close() {        this.param.selectTableData = {}        this.param.visible = false      },      // 获取项目树 下部门      getDataList() {        const url = this.api.travelExpense        let data = {          action: 'custaccbank',          data: {            pk_cust: this.pkCust          }        }        data.data = JSON.stringify(data.data)        this.$ajax.postform(url, data, res => {          if (res.success === 'Y') {            this.param.detailData = res.data || []          } else {            this.$message({ type: 'warn', message: res.message, duration: 1000 })          }        })      },      projectTableClick(row) {        this.param.selectTableData = row      },      confirm() {        if (!this.param.selectTableData.accountcode) {          return this.$message({ message: '请选择客商银行账号', duration: 1000 })        }        this.$emit('confirm', this.param.selectTableData)        this.close()      },    }  }</script><style scoped></style>