<template>  <div class="content c_flex c_flex_center">    <div class="list c_flex c_item_center c_flex_between">      <div class="opera prev" @click="prevImg"></div>      <div class="group c_flex_1 c_flex c_item_center">        <template v-for="(item,i) in list">          <div class="img c_flex c_flex_center c_whtie">            {{item.name}}          </div>        </template>      </div>      <div class="opera next" @click="nextImg"></div>    </div>  </div></template><script>    export default {        name: "test-2",      data(){          return {            list:[              {name:'img-1'},              {name:'img-2'},              {name:'img-3'},              {name:'img-4'},              {name:'img-5'},            ],          }      },      methods:{          prevImg(e){            let list = this.list;            let temp = list.shift();            list.push(temp);            this.list = list;          },          nextImg(e){            let list = this.list;            let temp = list.pop();            list.unshift(temp);            this.list = list;          },      },    }</script><style scoped>  .content{    width: 100%;    height: 100%;    background: #ccc;  }  .list{    width: 1000px;    height: 500px;  }  .img{    width: 200px;    height: 300px;    margin-right: 20px;    background: #fff;  }  .opera{    width: 0;    height: 0;    border: 30px solid transparent;  }  .opera.prev{    border-right-color: #2d8cff;  }  .opera.next{    border-left-color: #2d8cff;  }</style>